(window.webpackJsonp=window.webpackJsonp||[]).push([[37],{500:function(t,a,s){"use strict";s.r(a);var r=s(6),v=Object(r.a)({},(function(){var t=this,a=t.$createElement,s=t._self._c||a;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h2",{attrs:{id:"犬儒学派的来源"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#犬儒学派的来源"}},[t._v("#")]),t._v(" 犬儒学派的来源")]),t._v(" "),s("p",[t._v("犬儒学派指的是一个以狗为师、向狗学习的人。它的创始人"),s("strong",[t._v("安提斯泰尼")]),t._v("是苏格拉底的学生。")]),t._v(" "),s("p",[t._v("这个学派的特点就是看到一个人拥有功名富贵或各种社会成就，他就会冷嘲热讽，说一些尖酸刻薄的话，我们就用犬儒来描写他的愤世嫉俗，甚至自命清高。")]),t._v(" "),s("h2",{attrs:{id:"犬儒学派的主张"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#犬儒学派的主张"}},[t._v("#")]),t._v(" 犬儒学派的主张")]),t._v(" "),s("p",[t._v("安提斯泰尼是苏格拉底的学生，但是他只学到苏格拉底的一半。苏格拉底的思想有两面：破与立。“破”就是要破除流俗的各种价值观；“立”就是要建立自己的哲学，寻找人生最高的智慧。而犬儒学派只学到破的一面，却忘了特立独行另外有更高的目的。")]),t._v(" "),s("p",[t._v("犬儒学派认为人生的德行只有一个，就是节制自己的欲望进行苦修，要抛弃一般人所要的各种东西。他们认为只有伦理知识最有价值，伦理就是告诉你怎么做人处事，德行本身就足以带来幸福。")]),t._v(" "),s("p",[t._v("犬儒学派最广为人知的是一位比较稍后的学者，叫做迪奥真尼，与亚里士多德的年代差不多。迪奥真尼使得“犬儒”一词开始流行，他说要向动物学习，提倡原始的生活方式，以此和希腊文明对抗。因为所谓文明、文化都是虚伪的装饰品。迪奥真尼除了主张要向动物学习之外，还主张共妻、共子、自由恋爱等等。但他也强调，人只有节制欲望才能够自由。他自称为世界公民，要打破人间的各种隔阂樊篱。")]),t._v(" "),s("p",[t._v("有关他最有名的故事，就是亚历山大大帝统一了希腊各城邦之后，建立了马其顿帝国。当大帝来到雅典的时候，特意去拜访迪奥真尼，想请这位哲学家给他点开示。他走到迪奥真尼的木桶边很有礼貌地向他请教，迪奥真尼却只说了一句：“请你走开，不要挡住我的阳光。”")]),t._v(" "),s("h2",{attrs:{id:"犬儒学派地影响"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#犬儒学派地影响"}},[t._v("#")]),t._v(" 犬儒学派地影响")]),t._v(" "),s("p",[t._v("犬儒学派在罗马时代产生地影响，是有关人生实际的启发，告诉你如何生活才可以自足，并且可以凸显自己的人格特色，但在理论上并没有建构完整的系统。")]),t._v(" "),s("p",[t._v("到了18世纪，西方的启蒙运动出现了，对于当时的政治和宗教大加批判，其中有位学者卢梭，他批判文明是一切罪恶的来源。但是伏尔泰对卢梭的观点不敢苟同，他曾经以嘲讽的语气戏弄卢梭，说他是迪奥真尼的狗。当时还有一些学者，他们对于过度强调原始状态的美好，就批评说你就是迪奥真尼，应该躲在自己的木桶里不要出来。有关犬儒学派思潮地影响层出不穷。")])])}),[],!1,null,null,null);a.default=v.exports}}]);