(window.webpackJsonp=window.webpackJsonp||[]).push([[42],{505:function(t,v,_){"use strict";_.r(v);var a=_(6),r=Object(a.a)({},(function(){var t=this,v=t.$createElement,_=t._self._c||v;return _("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[_("h2",{attrs:{id:"享乐主义的来源"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#享乐主义的来源"}},[t._v("#")]),t._v(" 享乐主义的来源")]),t._v(" "),_("p",[t._v("享乐主义的创始人是伊壁鸠鲁，这位学者的年代比亚里士多德晚了40年左右。近代哲学家马克思所写的博士论文题目就是《德谟克里特的自然哲学与伊壁鸠鲁的自然哲学之比较》，代表伊壁鸠鲁的思想远承自古希腊时代的唯物论大师德谟克里特。")]),t._v(" "),_("p",[t._v("德谟克里特的原子论是古希腊时代具有代表性的唯物论的系统。他强调存在之物只有两个：一个是原子，一个是虚空。原子在虚空中的各种活动、碰撞，形成了宇宙万物，连人的灵魂都是原子构成的，这就是唯物论的思想。伊壁鸠鲁学习了德谟克里特的原子论，以这种思想为基础，再发展出他的享乐主义，把它运用到实际生活上。")]),t._v(" "),_("h2",{attrs:{id:"享乐主义的主张"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#享乐主义的主张"}},[t._v("#")]),t._v(" 享乐主义的主张")]),t._v(" "),_("p",[t._v("伊壁鸠鲁认为值得研究的只有三门学问：自然学、逻辑及伦理学。使用逻辑的思维方法来了解有形可见、充满变化的万物，就有了自然学，再由这种了解来指导你的实际生活，那就称作伦理学。")]),t._v(" "),_("p",[t._v("他对于哲学的作用，特别强调三点：")]),t._v(" "),_("ol",[_("li",[t._v("对死亡不必忧虑；")]),t._v(" "),_("li",[t._v("对神明不必害怕；")]),t._v(" "),_("li",[t._v("人生苦短，而哲学可以让你过得快乐。")])]),t._v(" "),_("p",[t._v("简单来说，享乐主义就是人生的快乐和痛苦完全在于感觉，在于现在的感觉。但是感觉不完全是指身体方面的状况，因为人在心理上照样可以透过回忆和预想来得到某种心理上的感受。这一来的话，我们就可以知道，他分析人活在世界上，没有任何超自然的力量在左右，也就是神明不会管人间的事情，所以人可以自由地追求快乐。")]),t._v(" "),_("p",[t._v("他的思想与德谟克里特的不同之处在于，伊壁鸠鲁认为人有自由，可以追求自己的快乐；而德谟克里特对人的自由不敢有这么大的信心。哲学就是要研究怎么样善用这种天赋的特权，透过理型思维与自由选择，让自己过得快乐。")]),t._v(" "),_("h2",{attrs:{id:"享乐主义的快乐是什么"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#享乐主义的快乐是什么"}},[t._v("#")]),t._v(" 享乐主义的快乐是什么")]),t._v(" "),_("ol",[_("li",[_("p",[t._v("快乐不是短暂的身体上的快乐，而是需要控制情绪与欲望。有时候要放弃小的快乐，以免后面有大的痛苦；有时候你要忍受小小的痛苦，来追求后面大的快乐。"),_("br"),t._v("\n所以你要判断小的快乐与小的痛苦，学习如何做选择。分辨快乐不是短暂的、身体上的快乐。换句话说，真正的快乐一定要考虑到一个行为的未来性，与他产生的效益的持久性，而不能只看当下的需求。因为人的欲望如果不节制的话会越来越大，开始像一颗小雪球，最后可以压垮一栋房子，而这样的欲望所带来的痛苦和烦恼是难以想象的。")])]),t._v(" "),_("li",[_("p",[t._v("心理上的快乐远远胜过身体上的快乐。为什么？因为身体本身没有记忆，也没有办法预想未来。身体本身不能做判断，需要理性来判断。也就是说人的心灵可以回忆过去和想象未来，所以理性的判断才能使你的身体免于痛苦、灵魂免于困惑。")])]),t._v(" "),_("li",[_("p",[t._v("真正的快乐不在于你要获得什么样的欲望满足，真正的快乐是消极的减少欲望，过简单的生活，让自己免于所有的痛苦与烦恼。享乐主义认为，朴素的生活、温和的脾气、节制自己的欲望是达到快乐地捷径，使得一个人生性看起来很淡泊的样子，对许多事情没有羡慕或嫉妒的问题。这种快乐显然是一种修养的结果，不是完全顺着天生的需求去发展。如果你要把快乐当作一个积极追求的目标，光是“要得到快乐”这几个字本身就会带来压力、烦恼、痛苦。")])]),t._v(" "),_("li",[_("p",[t._v("你对别人行善，要比你接受别人的善更为愉快。如果可以帮助别人，代表我有资源，如果是被帮助的话，代表我少这个少那个，当然不可能快乐。")])])])])}),[],!1,null,null,null);v.default=r.exports}}]);