(window.webpackJsonp=window.webpackJsonp||[]).push([[48],{535:function(v,_,t){"use strict";t.r(_);var a=t(1),s=Object(a.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("blockquote",[t("p",[v._v("19世纪的欧洲是德国唯心论以及浪漫主义运动发展的时代。虽然法国、英国都声光暗淡了，但任然值得简单介绍它的代表人物，也可以接到后来反对宗教而对唯物论的肯定。")])]),v._v(" "),t("h2",{attrs:{id:"孔德"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#孔德"}},[v._v("#")]),v._v(" 孔德")]),v._v(" "),t("p",[v._v("孔德认为科学进步之后可以取代哲学与宗教，提供我们在知识上的保障，也可以提高我们对自然界的掌握。所谓“实证主义”就是一切都要以可感觉的事实为出发点，并且限于可描述可感觉的事实及其规律。")]),v._v(" "),t("p",[v._v("这种实证主义跟科学上的实证方法不一样。实证方法是一个方法，对科学上的各种假设要进行验证，然后得到它的理论，那是方法。而实证主义则认为，只有直接表达感觉经验的陈述，或间接与这一类陈述有相同意义的命题，才有意义。换句话说，一个人说的话如果不能直接表达感觉经验，或不能间接与这种感觉经验的说法有关，那就是没有意义的。")]),v._v(" "),t("p",[v._v("实证主义受到很多人批评，因为他这句话本身就无法直接或间接与感觉经验有关，而纯粹是一套理论。")]),v._v(" "),t("p",[v._v("孔德最知名的主张就是他认为人类知识发展有三个阶段：神学、形而上学、科学。")]),v._v(" "),t("p",[v._v("针对科学，孔德有个特别的想法，就是要创造一种新的宗教，也就是人道宗教。他要选择历史上对人类的具体生活有所贡献的人来取代宗教里面的圣徒，他要把自然科学的方法运用到社会、道德各方面的研究。")]),v._v(" "),t("p",[v._v("这种人道宗教显然是一种主观的构想，甚至是幻想，因为这些对人类有所贡献的人物，彼此之间也有各种争议与矛盾。")]),v._v(" "),t("h2",{attrs:{id:"斯宾塞"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#斯宾塞"}},[v._v("#")]),v._v(" 斯宾塞")]),v._v(" "),t("p",[v._v("斯宾塞思想的特色是提出一个庞大的原则，他在学问上的吸收有限，但建构的很多，就像培根所谓的蜘蛛，负责吐丝结网。他在40岁的时候承认自己的生活可以用“杂”这个字来形容，就是大杂烩。但是他把进化论用在每一门学问上，他的代表作名为《综合哲学》，顾名思义就是把哲学所有的问题都整合起来加以处理。")]),v._v(" "),t("p",[v._v("斯宾塞的思想有两个重点：")]),v._v(" "),t("ol",[t("li",[t("p",[v._v("他认为在宗教与科学上，根本的观念都是不可知、不确定的。")])]),v._v(" "),t("li",[t("p",[v._v("他说：“既然这一切都不可确知，所以哲学就应该转向可知之物，它的任务就是要汇合及统一科学上的各种结论。”")])])]),v._v(" "),t("h2",{attrs:{id:"费尔巴哈"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#费尔巴哈"}},[v._v("#")]),v._v(" 费尔巴哈")]),v._v(" "),t("p",[v._v("费尔巴哈强调要回归自然界，因为自然界才是第一序的存在。至于人的思想和意识，那只是对自然界反思之后第二序的东西，所以人要特别掌握自己的感觉。他令人印象深刻的一句话是：真理、实在与感觉是同样的东西。感觉从来没有被提高到这么高的位置，它等于真理了。事实上，前面一些哲学家，尤其是英国经验主义，已经提到过一些类似的观念，不过费尔巴哈进一步加以落实，连宗教也可以做这样的解释。")]),v._v(" "),t("p",[v._v("费尔巴哈要以人类学取代神学。你只要把人类了解透彻，就可以充分知道神是怎么回事，不是上帝造了人，而是人造了上帝。")]),v._v(" "),t("p",[v._v("人类的未来要往哪里走呢？只有一条出路，就是回到人类自己的世界，人与人好好相处，这样就可以建构一个理想的政治结构，组成一个理想的国家，在这方面继续追求人生的幸福。")]),v._v(" "),t("h2",{attrs:{id:"马克思"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#马克思"}},[v._v("#")]),v._v(" 马克思")]),v._v(" "),t("div",{staticClass:"custom-block tip"},[t("p",[v._v("哲学家们只是用不同的方式解释世界，而问题在于改变世界。")]),v._v(" "),t("div",{staticClass:"custom-block right"},[t("p",[v._v("—— 马克思")])])]),v._v(" "),t("h3",{attrs:{id:"马克思主张唯物论"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#马克思主张唯物论"}},[v._v("#")]),v._v(" 马克思主张唯物论")]),v._v(" "),t("p",[v._v("马克思进入大学之后，成为一个无神论者。因为对于一个犹太人来说，在犹太教与基督宗教之间，两边都让让他有很大的压力。")]),v._v(" "),t("p",[v._v("马克思的博士论文题目是《德谟克里特的自然哲学与伊比鸠鲁的自然哲学之比较》。德谟克里特是古希腊时代标准的唯物论者，主张原子论；伊壁鸠鲁受到德谟克里特的启发，把他的唯物论应用在人生哲学上。马克思研究这两位古代唯物论的思想，自然深受启发。")]),v._v(" "),t("h3",{attrs:{id:"唯物论的新看法"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#唯物论的新看法"}},[v._v("#")]),v._v(" 唯物论的新看法")]),v._v(" "),t("p",[v._v("我们谈到费尔巴哈的时候，强调他是明显的唯物论。要以自然界作为第一序的实体。马克思进一步质疑费尔巴哈，他说：“难道他没有注意到人对自然界的创造及改造的作用吗？”所以马克思公开说：“从前所有的唯物论，他们主要有一个缺点，就是对于事物、现实与感性，只从客观角度或直觉的形式去了解，而没有把他们当作是人的感性活动或当作人的实践去了解。”")]),v._v(" "),t("p",[v._v("换句话说，马克思认为真正的唯物论不应该只是把我们所面对的万物当作一个与我们人类无关的对象去观察与了解。也就是说，在观察自然界的时候，不能把人的活动撇开。真正的唯物论应该把自然界以及人类在自然界的各种活动配合起来看。")]),v._v(" "),t("p",[v._v("马克思认为，人对自然界的认识是通过人的实践才有可能的。譬如，在农耕社会，你如果没有实践去耕田，怎么可能认识自然界是怎么回事呢？同时，他也强调，自然界也正是由于过去人类的实践，才使得它有人的味道，也就是成为人化的自然界。")]),v._v(" "),t("p",[v._v("马克思又进一步强调，人是在一定的、具体的社会关系中，有意识地进行这样的活动。如此一来，他就把过去的唯物论加以扩充，使他不再是一种狭隘的观念，而是成为全面的、完整的体系，也就是加进了人与社会的因素，这可以说是马克思的新见解。")]),v._v(" "),t("p",[t("strong",[v._v("马克思就以这种唯物论为基础，接着提出他有关劳动生产、劳动创造的观点，这是与人性有关的。马克思在《政治经济学批判》这本书的开头就说了一句非常著名的话“物质生活的生产方式制约着整个社会生活、政治生活与精神生活的过程。不是人们的意识决定人们的存在，而是人们的社会存在决定人们的意识。”这句话简单来说就是，你有什么想法决定于你生活在什么样的时代、你从事什么样的工作。所以马克思在强调唯物论的时候，到最后焦点就转向人具体的社会生活。")])]),v._v(" "),t("p",[v._v("人们的社会存在决定了人们的意识，这是他主要的观点。譬如在不同的阶段，像渔猎社会和农耕社会一路发展下来，物质生活的生产方式、经济条件与经济结构，就决定了上层的各种政治、社会与精神生活的表现。")]),v._v(" "),t("h3",{attrs:{id:"唯物史观"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#唯物史观"}},[v._v("#")]),v._v(" 唯物史观")]),v._v(" "),t("p",[v._v("马克思认为历史的发展根源在于生产力与生产关系之间的矛盾。在农耕、渔猎或封建等社会阶段中，生产力是人所拥有的一切条件，可以在生产活动中使用来作为工具的。而生产关系指的是在劳动过程中包含的人与人之间的社会关系。生产力与生产关系两者之间有矛盾存在。马克思就以这个作为基础，把历史的发展分为五个阶段：")]),v._v(" "),t("ol",[t("li",[t("p",[v._v("原始共产社会，他以早期的亚洲为例，说在古代是氏族组织，由人们共同拥有土地，联合生产，还没有出现私有财产的问题；")])]),v._v(" "),t("li",[t("p",[v._v("奴隶社会，他以希腊及罗马时期作为例证；")])]),v._v(" "),t("li",[t("p",[v._v("资本主义社会，也就是马克思身处的历史阶段。其中有各种问题，使得马克思认为要通过革命的手段才能实现无产阶级专政；")])]),v._v(" "),t("li",[t("p",[v._v("共产主义社会，马克思相信未来会出现的社会。")])])]),v._v(" "),t("p",[v._v("这是马克思很有特色的观点，就是历史唯物论。马克思这种观点也许会受到某些历史学家的质疑，但不可否认的是，他对社会与历史问题采取了新的态度，进而为人类的知识打开了新的途径，所以值得注意。")])])}),[],!1,null,null,null);_.default=s.exports}}]);